import React, { Component } from 'react'
import GoogleLogin from 'react-google-login'
import $ from "jquery";
import './Insurance.css';

export class Insurance extends Component {
    state = {
        response: '',
        name: '',
        email: '',
        password: '',
        responseToPost: '',
    };
    //   componentDidMount() {
    //     this.callApi()
    //       .then(res => this.setState({ response: res.express }))
    //       .catch(err => console.log(err));
    //   }
    //   callApi = async () => {
    //     const response = await fetch('/Auth');
    //     const body = await response.json();
    //     if (response.status !== 200) throw Error(body.message);
    //     return body;
    //   };
    handleSubmit = async e => {
        e.preventDefault();
        const response = await fetch('/Auth/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ password: this.state.password, email: this.state.email, name: this.state.name }),
        });
        const body = await response.text();
        this.setState({ responseToPost: body });
    };


    // document.getElementById('.img-btn').addEventListener('click', function()
    // 	{
    // 		document.getElementById('.cont').classList.toggle('s-signup')
    // 	}
    // );
    componentDidMount() {
        $('.img-btn').click(function () {
            $('.cont').toggleClass('s-signup');
        });
    }

    responseGoogle = (response) => {
        console.log(response);
        console.log(response.profileObj);
    }
    render() {
        return (
            <div class="auth">
                <div class="cont">
                    <form class="form" >
                        <h2> Sign In </h2>
                        <label >
                            <span>Your Name</span>
                            <input type="text" name="name"
                                // placeholder="email@gmail.com"
                                value={this.state.name}
                                onChange={e => this.setState({ name: e.target.value })}
                            />
                        </label>
                        <label>
                            <span>Email Address</span>
                            <input type="email" name="email"
                                // placeholder="email@gmail.com"
                                value={this.state.email}
                                onChange={e => this.setState({ email: e.target.value })}
                            />
                        </label>
                        <label>
                            <span> password </span>
                            <input type="password"
                                value={this.state.password}
                                onChange={e => this.setState({ password: e.target.value })}
                            />
                        </label>
                        <button class="submit" type="button" onClick={this.handleSubmit}> Sign In </button>
                        <p class="forgot-pass"> Forgot Password ? </p>
                        <p>{this.state.responseToPost}</p>

                    </form>
                </div>
            </div>
        )
    }
}
// document.getElementById('.img-btn').addEventListener('click', function()
// 	{
// 		document.getElementById('.cont').classList.toggle('s-signup')
// 	}
// );

export default Insurance
